mapper.select:
SELECT	GATE_ID
		,GATE_DESC
FROM	GATE
WHERE	1 = 1
		
		AND	GATE_ID = #{gateId}		
		
		GATE_DESC = #{gateDesc}

mapper.update:
UPDATE	GATE
SET		GATE_DESC = #{gateDesc}
		, GATE_PINGED_DATE = #{gatePingedDate}
WHERE	GATE_ID = #{gateId}

